import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import ReactPractice from './Components/ReactPractice';
import axios from 'axios';
import './App.css';
import './index.css';


function App() {

  const [user, setUser] = useState([]);

  useEffect(() => {
    fetchDataByAxios();

  }, []);

  const fetchDataByAxios = async () => {
    let response = await axios({
      mehtod: 'get',
      url: '/5004333890',
      headers: {
        'Access-Control-Allow-Origin': '*',
        'Authorization': 'Bearer rpdcmabeRrjXQ-UgwyznejRBazDCYPRCdxxuVRC-Zq0.KgbJfhEWMX19eLrsyD_5TQEWm7vpl-CrTfJhTMjhY2c',
        'Content-Type': 'application/x-www-form-urlencoded',
      },

    }).then((response) => {
      const allData = response.data;
      setUser(allData);
      console.log(allData);
    }).catch(error => console.error(`Error:${error}`));
    console.log(window.location.href)
  };

  if(!user){
    return null;
  }

  // 'Content-Type': 'application/json',
  // baseURL: 'https://mitarbeiterwebservice.maklerinfo.biz/service/ari/employee/1.0/rest/Alpha0_BQ5G3X/kunden'

  return (
    <div>
      <ReactPractice />

    <div>

    {
      user ? (
        user.map((data) =>{return (

      <div className="data" key={data.Id}>
      <h3>{data.Person.Vorname} {data.Person.Nachname}</h3>

    </div>);

        })):(<h3>No data yet</h3>)}


    </div>




    </div>
  );
}
export default App;

