import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import ReactPractice from './Components/ReactPractice';
import axios from 'axios';
import './App.css';
import './index.css';


function App() {

  const [user, setUser] = useState([]);

  useEffect(() => {
    fetchDataByAxios();

  }, []);

  const fetchDataByAxios = async () => {
    let response = await axios({
      mehtod: 'get',
      url: '/5004333890',
      headers: {
        'Access-Control-Allow-Origin': '*',
        'Authorization': 'Bearer rpdcmabeRrjXQ-UgwyznejRBazDCYPRCdxxuVRC-Zq0.KgbJfhEWMX19eLrsyD_5TQEWm7vpl-CrTfJhTMjhY2c',
        'Content-Type': 'application/x-www-form-urlencoded',
      },

    }).then((response) => {
      const allData = response.data;
      setUser(allData);
      console.log(allData);
    }).catch(error => console.error(`Error:${error}`));
    console.log(window.location.href)
  };

  if (!user) {
    return null;
  }

  // 'Content-Type': 'application/json',
  // baseURL: 'https://mitarbeiterwebservice.maklerinfo.biz/service/ari/employee/1.0/rest/Alpha0_BQ5G3X/kunden'

  return (
    <div>
      <ReactPractice />

      <div>
        {
          user ? (
            user.map((user) => {
              return (

                // <div className="data" key={data.Id}>
                //   <h3>{data.Person.Vorname} {data.Person.Nachname}</h3>

                // </div>


                <div key={data.Id} className="container d-flex justify-content-center align-items-center">

                  <div className="card">

                    <div className="upper">

                      <img src="https://i.imgur.com/Qtrsrk5.jpg" className="img-fluid" />

                    </div>

                    <div className="user text-center">

                      <div className="profile">

                        <img src="https://i.imgur.com/JgYD2nQ.jpg" className="rounded-circle" width="80" />

                      </div>

                    </div>


                    <div className="mt-5 text-center">

                      <h4 className="mb-0">{user.Person.Vorname} {user.Person.Nachname}</h4>
                      <span className="text-muted d-block mb-2">Los Angles</span>

                      <button className="btn btn-primary btn-sm follow">Follow</button>


                      <div className="d-flex justify-content-between align-items-center mt-4 px-4">

                        <div className="stats">
                          <h6 className="mb-0">Followers</h6>
                          <span>8,797</span>

                        </div>


                        <div className="stats">
                          <h6 className="mb-0">Projects</h6>
                          <span>142</span>

                        </div>


                        <div className="stats">
                          <h6 className="mb-0">Ranks</h6>
                          <span>129</span>

                        </div>

                      </div>

                    </div>

                  </div>

                </div>








              );

            })) : (<h3>No data yet</h3>)
        }


      </div>

    </div>
  );
}
export default App;

