import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdressenComponent() {

    const [data, setData] = useState('');

    useEffect(() => {
        fetchDataByAxios();
    }, []);

    let access_token = 'Bearer QD3SB-VG2s-ZyUitdCmI-SEqBCF2e3WtHyNzbhNmdEY.1kPd-IGJo7j5I5XKf08ki6Ifrhgzc9f6tTJnMqsxnuE';

    const fetchDataByAxios = async () => {
        let response = await axios({
            mehtod: 'get',
            url: '/5004333890/adressen',
            headers: {
                'Access-Control-Allow-Origin': '*',
                'Authorization': access_token,
                'Content-Type': 'application/x-www-form-urlencoded',
            },

        }).then((response) => {
            const allData = response.data;
            setData(allData);
            console.log(allData);
        }).catch(error => console.error(`Error:${error}`));
        console.log(window.location.href)
    };



    // 'Content-Type': 'application/json',
    // baseURL: 'https://mitarbeiterwebservice.maklerinfo.biz/service/ari/employee/1.0/rest/Alpha0_BQ5G3X/kunden'

    return (

        <div className="row row-cols-1 row-cols-md-2 g-1 own_contianer p-2">
            {
                data ? (
                    data.map((data) => {
                        return (
                            
                        <div className="card  m-1 border-info adrInfo cardbodykon" style="max-width: 18rem;">
                            <div className="card-header font-weight-bold" style="text-transform: uppercase; font-size : 13px; padding-bottom:2px; border-bottom : 0px;"><b>&nbsp;{data.Typ}</b>
                            </div>

                            <div className="card-body text-dark">
                                <h5 className="card-title">{data.Strasse}</h5>
                            </div>

                            <div className="card-footer" style="background-color:transparent;border-top : 0px;">
                                <p className="card-text"><span class="region">{data.Postleitzahl}&nbsp;{data.Ort}</span></p>
                            </div>
                        </div>

                        )
                    })

                ) : (<div className="card  m-1 border-info adrInfo cardbodykon" style="max-width: 18rem;">
                         <div className="card-header font-weight-bold" style="text-transform: uppercase; font-size : 13px; padding-bottom:2px; border-bottom : 0px;"><b>&nbsp;niederlassung</b>
                             </div>

                             <div className="card-body text-dark">
                                <h5 className="card-title">no Data</h5>
                                </div>

                              <div className="card-footer" style="background-color:transparent;border-top : 0px;">
                                <p className="card-text"><span class="region">no Data </span></p>
                                </div>
                         </div>)
            }
        </div>
    );
}
export default AdressenComponent;

